class Rating{constructor(e){e.innerHTML='<div style="width: 172px; height: 24px;"></div>',this.div=e.querySelector("div");for(var t=0;t<5;t++)this.div.innerHTML+=`<div data-value="${t+1}">`;this.render()}change(e){e=e.target.dataset.value;e&&(this.svg.parentNode.dataset.value=e),this.render()}render(){this.div.querySelectorAll("div").forEach(e=>{var t=+this.div.parentNode.dataset.value>=+e.dataset.value;e.classList.toggle("active",t)})}}document.querySelectorAll(".rating").forEach(e=>new Rating(e));const langue=document?.querySelector(".product-list__langue"),langueRu=document.querySelector(".langue-ru"),langueEn=document.querySelector(".langue-en");langue.addEventListener("click",()=>{langueRu.classList.contains("active")?(langueRu.classList.remove("active"),langueEn.classList.add("active")):(langueRu.classList.add("active"),langueEn.classList.remove("active"))});